import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [latestData, setLatestData] = useState(null);
  const [historyData, setHistoryData] = useState([]);

  // Fetch the latest data
  useEffect(() => {
    fetch("http://localhost:3001/api/temperature")  // Corrected to use backend port 3001
      .then((response) => response.json())
      .then((data) => setLatestData(data))
      .catch((error) => console.error("Error fetching latest data:", error));
  }, []);

  // Fetch historical data
  useEffect(() => {
    fetch("http://localhost:3001/api/history")  // Corrected to use backend port 3001
      .then((response) => response.json())
      .then((data) => setHistoryData(data))
      .catch((error) => console.error("Error fetching historical data:", error));
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <h1>Temperature and Humidity Monitor</h1>
      </header>
      <main>
        <section>
          <h2>Latest Data</h2>
          {latestData ? (
            <div>
              <p>Temperature: {latestData.temperature}°C</p>
              <p>Humidity: {latestData.humidity}%</p>
              <p>Timestamp: {new Date(latestData.timestamp).toLocaleString()}</p>
            </div>
          ) : (
            <p>Loading latest data...</p>
          )}
        </section>
        <section>
          <h2>Historical Data (Last 50 Records)</h2>
          {historyData.length > 0 ? (
            <table>
              <thead>
                <tr>
                  <th>Timestamp</th>
                  <th>Temperature (°C)</th>
                  <th>Humidity (%)</th>
                </tr>
              </thead>
              <tbody>
                {historyData.map((record, index) => (
                  <tr key={index}>
                    <td>{new Date(record.timestamp).toLocaleString()}</td>
                    <td>{record.temperature}</td>
                    <td>{record.humidity}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p>Loading historical data...</p>
          )}
        </section>
      </main>
    </div>
  );
}

export default App;
